import { db } from '../server/db';
import { 
  users, 
  reviewCycles, 
  employeeReviews,
  feedback,
  activityLogs
} from '../shared/schema';

async function seedTestData() {
  console.log('üå± Seeding test data...');

  try {
    // Create test users with hierarchical structure
    console.log('Creating users...');

    // Founder (Harry)
    const harry = await db.insert(users).values({
      id: 'harry-founder-001',
      email: 'harry@company.com',
      firstName: 'Harry',
      lastName: 'Founder',
      role: 'founder',
      department: 'Executive',
      isActive: true,
    }).returning();

    // L1 Manager
    const l1Manager = await db.insert(users).values({
      id: 'l1-manager-001',
      email: 'l1manager@company.com',
      firstName: 'Alice',
      lastName: 'L1Manager',
      role: 'l1_manager',
      department: 'Engineering',
      managerId: harry[0].id,
      isActive: true,
    }).returning();

    // L2 Managers
    const l2Manager1 = await db.insert(users).values({
      id: 'l2-manager-001',
      email: 'l2manager1@company.com',
      firstName: 'Bob',
      lastName: 'L2Manager',
      role: 'l2_manager',
      department: 'Engineering',
      managerId: l1Manager[0].id,
      isActive: true,
    }).returning();

    const l2Manager2 = await db.insert(users).values({
      id: 'l2-manager-002',
      email: 'l2manager2@company.com',
      firstName: 'Carol',
      lastName: 'L2Manager',
      role: 'l2_manager',
      department: 'Design',
      managerId: l1Manager[0].id,
      isActive: true,
    }).returning();

    // L3 Managers
    const l3Manager1 = await db.insert(users).values({
      id: 'l3-manager-001',
      email: 'l3manager1@company.com',
      firstName: 'David',
      lastName: 'L3Manager',
      role: 'l3_manager',
      department: 'Engineering',
      managerId: l2Manager1[0].id,
      isActive: true,
    }).returning();

    const l3Manager2 = await db.insert(users).values({
      id: 'l3-manager-002',
      email: 'l3manager2@company.com',
      firstName: 'Eve',
      lastName: 'L3Manager',
      role: 'l3_manager',
      department: 'Design',
      managerId: l2Manager2[0].id,
      isActive: true,
    }).returning();

    // Peers - Software Developers
    const dev1 = await db.insert(users).values({
      id: 'dev-001',
      email: 'john.dev@company.com',
      firstName: 'John',
      lastName: 'Developer',
      role: 'peer',
      category: 'software_developer',
