import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { z } from "zod";
import { 
  insertEmployeeReviewSchema,
  insertFeedbackSchema,
  insertMeetingSchema,
  insertAppealSchema,
  insertReviewCycleSchema 
} from "@shared/schema";
import { sendReviewNotification, sendMeetingInvitation, sendAppealNotification } from "./emailService";

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Dashboard routes
  app.get('/api/dashboard/stats', isAuthenticated, async (req: any, res) => {
    try {
      const cycle = await storage.getActiveReviewCycle();
      if (!cycle) {
        return res.json({ totalEmployees: 0, completedReviews: 0, pendingApprovals: 0, averageRating: 0 });
      }

      const stats = await storage.getReviewStats(cycle.id);
      const pendingApprovals = await storage.getPendingApprovals(req.user.claims.sub);

      res.json({
        ...stats,
        pendingApprovals: pendingApprovals.length,
      });
    } catch (error) {
      console.error("Error fetching dashboard stats:", error);
      res.status(500).json({ message: "Failed to fetch dashboard stats" });
    }
  });

  app.get('/api/dashboard/pending-actions', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      const pendingActions = [];

      // Get pending approvals for managers
      if (user.role === 'l1_manager' || user.role === 'l2_manager') {
        const pendingApprovals = await storage.getPendingApprovals(userId);
        pendingActions.push(...pendingApprovals.map(review => ({
          type: 'approval',
          data: review,
        })));
      }

      // Get pending appeals
      if (user.role === 'l2_manager' || user.role === 'founder') {
        const pendingAppeals = await storage.getAppealsByManager(userId);
        pendingActions.push(...pendingAppeals.filter(appeal => appeal.status === 'pending').map(appeal => ({
          type: 'appeal',
          data: appeal,
        })));
      }

      res.json(pendingActions);
    } catch (error) {
      console.error("Error fetching pending actions:", error);
      res.status(500).json({ message: "Failed to fetch pending actions" });
    }
  });

  app.get('/api/dashboard/recent-activities', isAuthenticated, async (req: any, res) => {
    try {
      const activities = await storage.getRecentActivities(10);
      res.json(activities);
    } catch (error) {
      console.error("Error fetching recent activities:", error);
      res.status(500).json({ message: "Failed to fetch recent activities" });
    }
  });

  app.get('/api/dashboard/analytics/rating-distribution', isAuthenticated, async (req: any, res) => {
    try {
      const cycle = await storage.getActiveReviewCycle();
      if (!cycle) {
        return res.json([]);
      }

      const distribution = await storage.getRatingDistribution(cycle.id);
      res.json(distribution);
    } catch (error) {
      console.error("Error fetching rating distribution:", error);
      res.status(500).json({ message: "Failed to fetch rating distribution" });
    }
  });

  app.get('/api/dashboard/analytics/department-performance', isAuthenticated, async (req: any, res) => {
    try {
      const cycle = await storage.getActiveReviewCycle();
      if (!cycle) {
        return res.json([]);
      }

      const performance = await storage.getDepartmentPerformance(cycle.id);
      res.json(performance);
    } catch (error) {
      console.error("Error fetching department performance:", error);
      res.status(500).json({ message: "Failed to fetch department performance" });
    }
  });

  // Review cycle management
  app.post('/api/review-cycles', isAuthenticated, async (req: any, res) => {
    try {
      const user = await storage.getUser(req.user.claims.sub);
      if (user?.role !== 'founder') {
        return res.status(403).json({ message: "Only founder can create review cycles" });
      }

      const cycleData = insertReviewCycleSchema.parse(req.body);
      const cycle = await storage.createReviewCycle(cycleData);

      res.status(201).json(cycle);
    } catch (error) {
      console.error("Error creating review cycle:", error);
      res.status(500).json({ message: "Failed to create review cycle" });
    }
  });

  app.get('/api/review-cycles/active', isAuthenticated, async (req: any, res) => {
    try {
      const cycle = await storage.getActiveReviewCycle();
      res.json(cycle);
    } catch (error) {
      console.error("Error fetching active review cycle:", error);
      res.status(500).json({ message: "Failed to fetch active review cycle" });
    }
  });

  // Employee review management
  app.get('/api/reviews/my-review', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const cycle = await storage.getActiveReviewCycle();

      if (!cycle) {
        return res.status(404).json({ message: "No active review cycle" });
      }

      const review = await storage.getEmployeeReview(userId, cycle.id);
      res.json(review);
    } catch (error) {
      console.error("Error fetching employee review:", error);
      res.status(500).json({ message: "Failed to fetch employee review" });
    }
  });

  app.get('/api/reviews/current', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const cycle = await storage.getActiveReviewCycle();

      if (!cycle) {
        return res.json(null);
      }

      const review = await storage.getEmployeeReview(userId, cycle.id);

      res.json({
        cycleName: cycle.name,
        selfAssessmentData: review?.selfAssessmentData || null,
        status: review?.status || 'not_started',
        reviewId: review?.id,
      });
    } catch (error) {
      console.error("Error fetching current review:", error);
      res.status(500).json({ message: "Failed to fetch current review" });
    }
  });

  app.post('/api/reviews/self-assessment', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const cycle = await storage.getActiveReviewCycle();

      if (!cycle) {
        return res.status(404).json({ message: "No active review cycle" });
      }

      let review = await storage.getEmployeeReview(userId, cycle.id);

      if (!review) {
        // Create new review if it doesn't exist
        review = await storage.createEmployeeReview({
          employeeId: userId,
          cycleId: cycle.id,
          status: 'self_assessment',
        });
      }

      const updatedReview = await storage.updateEmployeeReview(review.id, {
        selfAssessmentData: req.body,
        status: 'feedback_collection',
      });

      // Log activity
      await storage.logActivity({
        userId,
        action: 'self_assessment_completed',
        description: 'Completed self-assessment',
        relatedEntityType: 'review',
        relatedEntityId: review.id,
      });

      res.json(updatedReview);
    } catch (error) {
      console.error("Error saving self-assessment:", error);
      res.status(500).json({ message: "Failed to save self-assessment" });
    }
  });

  // 360-degree feedback
  app.post('/api/feedback', isAuthenticated, async (req: any, res) => {
    try {
      const feedbackData = insertFeedbackSchema.parse({
        ...req.body,
        feedbackFromId: req.user.claims.sub,
      });

      const feedback = await storage.createFeedback(feedbackData);

      // Log activity
      await storage.logActivity({
        userId: req.user.claims.sub,
        action: 'feedback_submitted',
        description: 'Submitted 360-degree feedback',
        relatedEntityType: 'feedback',
        relatedEntityId: feedback.id,
      });

      res.status(201).json(feedback);
    } catch (error) {
      console.error("Error creating feedback:", error);
      res.status(500).json({ message: "Failed to create feedback" });
    }
  });

  app.get('/api/feedback/pending', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const cycle = await storage.getActiveReviewCycle();

      if (!cycle) {
        return res.json([]);
      }

      const givenFeedback = await storage.getFeedbackByUser(userId, cycle.id);
      // TODO: Implement logic to find pending feedback requests

      res.json([]);
    } catch (error) {
      console.error("Error fetching pending feedback:", error);
      res.status(500).json({ message: "Failed to fetch pending feedback" });
    }
  });

  app.get('/api/feedback/targets', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      const cycle = await storage.getActiveReviewCycle();

      if (!cycle || !user) {
        return res.json([]);
      }

      // Get team members for 360-degree feedback
      const targets = [];

      if (user.role === 'peer') {
        // Peers can give feedback to other peers in the same team
        const teamMembers = await storage.getUsersByManager(user.managerId!);

        for (const member of teamMembers) {
          if (member.id !== userId) {
            const review = await storage.getEmployeeReview(member.id, cycle.id);
            if (review) {
              // Check if feedback already given
              const existingFeedback = await storage.getFeedback(review.id, userId);

              targets.push({
                reviewId: review.id,
                employeeName: `${member.firstName} ${member.lastName}`,
                employeeRole: member.category || 'Employee',
                department: member.department,
                existingFeedback: existingFeedback,
              });
            }
          }
        }
      }

      res.json(targets);
    } catch (error) {
      console.error("Error fetching feedback targets:", error);
      res.status(500).json({ message: "Failed to fetch feedback targets" });
    }
  });

  // Meeting management
  app.post('/api/meetings', isAuthenticated, async (req: any, res) => {
    try {
      const meetingData = insertMeetingSchema.parse({
        ...req.body,
        managerId: req.user.claims.sub,
      });

      const meeting = await storage.createMeeting(meetingData);

      // Send meeting invitation
      const employee = await storage.getUser(meeting.employeeId);
      const manager = await storage.getUser(meeting.managerId);

      if (employee?.email && manager) {
        await sendMeetingInvitation(
          employee.email,
          `${employee.firstName} ${employee.lastName}`,
          `${manager.firstName} ${manager.lastName}`,
          meeting.scheduledAt.toISOString(),
          meeting.meetingLink || undefined
        );
      }

      // Log activity
      await storage.logActivity({
        userId: req.user.claims.sub,
        action: 'meeting_scheduled',
        description: `Scheduled 1:1 meeting with ${employee?.firstName} ${employee?.lastName}`,
        relatedEntityType: 'meeting',
        relatedEntityId: meeting.id,
      });

      res.status(201).json(meeting);
    } catch (error) {
      console.error("Error creating meeting:", error);
      res.status(500).json({ message: "Failed to create meeting" });
    }
  });

  app.get('/api/meetings/my-meetings', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      let meetings;
      if (user.role === 'peer') {
        meetings = await storage.getMeetingsByEmployee(userId);
      } else {
        meetings = await storage.getMeetingsByManager(userId);
      }

      res.json(meetings);
    } catch (error) {
      console.error("Error fetching meetings:", error);
      res.status(500).json({ message: "Failed to fetch meetings" });
    }
  });

  // Appeal management
  app.post('/api/appeals', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const cycle = await storage.getActiveReviewCycle();

      if (!cycle) {
        return res.status(404).json({ message: "No active review cycle" });
      }

      const review = await storage.getEmployeeReview(userId, cycle.id);

      if (!review) {
        return res.status(404).json({ message: "Review not found" });
      }

      if (review.appealUsed) {
        return res.status(400).json({ message: "Appeal already used for this review" });
      }

      const appealData = insertAppealSchema.parse({
        ...req.body,
        reviewId: review.id,
        employeeId: userId,
      });

      const appeal = await storage.createAppeal(appealData);

      // Mark appeal as used
      await storage.updateEmployeeReview(review.id, {
        appealUsed: true,
        status: 'appeal_requested',
      });

      // Send notification to manager
      const employee = await storage.getUser(userId);
      const manager = await storage.getUser(employee?.managerId || '');

      if (manager?.email && employee) {
        await sendAppealNotification(
          manager.email,
          `${employee.firstName} ${employee.lastName}`,
          `${manager.firstName} ${manager.lastName}`,
          appeal.reason
        );
      }

      // Log activity
      await storage.logActivity({
        userId,
        action: 'appeal_submitted',
        description: 'Submitted appeal request',
        relatedEntityType: 'appeal',
        relatedEntityId: appeal.id,
      });

      res.status(201).json(appeal);
    } catch (error) {
      console.error("Error creating appeal:", error);
      res.status(500).json({ message: "Failed to create appeal" });
    }
  });

  app.put('/api/appeals/:id', isAuthenticated, async (req: any, res) => {
    try {
      const { id } = req.params;
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (user?.role !== 'l2_manager' && user?.role !== 'founder') {
        return res.status(403).json({ message: "Not authorized to handle appeals" });
      }

      const appeal = await storage.updateAppeal(id, {
        ...req.body,
        managerId: userId,
      });

      // Log activity
      await storage.logActivity({
        userId,
        action: 'appeal_processed',
        description: `Processed appeal: ${appeal.status}`,
        relatedEntityType: 'appeal',
        relatedEntityId: appeal.id,
      });

      res.json(appeal);
    } catch (error) {
      console.error("Error updating appeal:", error);
      res.status(500).json({ message: "Failed to update appeal" });
    }
  });

  // User management
  app.get('/api/users/team', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      let team = [];

      if (user.role === 'founder') {
        team = await storage.getUsersByRole('l1_manager');
      } else if (user.role === 'l1_manager') {
        team = await storage.getUsersByRole('l2_manager');
      } else if (user.role === 'l2_manager') {
        team = await storage.getUsersByRole('l3_manager');
        const peers = await storage.getUsersByRole('peer');
        team = [...team, ...peers];
      } else if (user.role === 'l3_manager') {
        team = await storage.getDirectReports(userId);
      }

      res.json(team);
    } catch (error) {
      console.error("Error fetching team:", error);
      res.status(500).json({ message: "Failed to fetch team" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import {
  users,
  reviewCycles,
  employeeReviews,
  feedback,
  meetings,
  appeals,
  activityLogs,
  type User,
  type UpsertUser,
  type InsertUser,
  type ReviewCycle,
  type InsertReviewCycle,
  type EmployeeReview,
  type InsertEmployeeReview,
  type Feedback,
  type InsertFeedback,
  type Meeting,
  type InsertMeeting,
  type Appeal,
  type InsertAppeal,
  type ActivityLog,
  type InsertActivityLog,
} from "@shared/schema";
import { db } from "./db";
import { eq, and, or, desc, asc, count, avg } from "drizzle-orm";

export interface IStorage {
  // User operations (required for Replit Auth)
  getUser(id: string): Promise<User | undefined>;
  upsertUser(user: UpsertUser): Promise<User>;

  // User management
  createUser(user: InsertUser): Promise<User>;
  updateUser(id: string, data: Partial<InsertUser>): Promise<User>;
  getUsersByRole(role: string): Promise<User[]>;
  getUsersByManager(managerId: string): Promise<User[]>;
  getDirectReports(managerId: string): Promise<User[]>;

  // Review cycle management
  createReviewCycle(cycle: InsertReviewCycle): Promise<ReviewCycle>;
  getActiveReviewCycle(): Promise<ReviewCycle | undefined>;
  getAllReviewCycles(): Promise<ReviewCycle[]>;
  updateReviewCycle(id: string, data: Partial<InsertReviewCycle>): Promise<ReviewCycle>;

  // Employee review management
  createEmployeeReview(review: InsertEmployeeReview): Promise<EmployeeReview>;
  getEmployeeReview(employeeId: string, cycleId: string): Promise<EmployeeReview | undefined>;
  getEmployeeReviewById(id: string): Promise<EmployeeReview | undefined>;
  updateEmployeeReview(id: string, data: Partial<InsertEmployeeReview>): Promise<EmployeeReview>;
  getReviewsByManager(managerId: string, cycleId: string): Promise<EmployeeReview[]>;
  getReviewsByStatus(status: string, cycleId: string): Promise<EmployeeReview[]>;
  getPendingApprovals(managerId: string): Promise<EmployeeReview[]>;

  // 360-degree feedback
  createFeedback(feedbackData: InsertFeedback): Promise<Feedback>;
  getFeedbackForReview(reviewId: string): Promise<Feedback[]>;
  getFeedbackByUser(userId: string, cycleId: string): Promise<Feedback[]>;

  // Meeting management
  createMeeting(meeting: InsertMeeting): Promise<Meeting>;
  getMeetingsByEmployee(employeeId: string): Promise<Meeting[]>;
  getMeetingsByManager(managerId: string): Promise<Meeting[]>;
  updateMeeting(id: string, data: Partial<InsertMeeting>): Promise<Meeting>;

  // Appeal management
  createAppeal(appeal: InsertAppeal): Promise<Appeal>;
  getAppealsByEmployee(employeeId: string): Promise<Appeal[]>;
  getAppealsByManager(managerId: string): Promise<Appeal[]>;
  updateAppeal(id: string, data: Partial<InsertAppeal>): Promise<Appeal>;

  // Activity logging
  logActivity(activity: InsertActivityLog): Promise<ActivityLog>;
  getRecentActivities(limit?: number): Promise<ActivityLog[]>;
  getActivitiesByUser(userId: string): Promise<ActivityLog[]>;

  // Analytics
  getReviewStats(cycleId: string): Promise<any>;
  getRatingDistribution(cycleId: string): Promise<any>;
  getDepartmentPerformance(cycleId: string): Promise<any>;
}

export class DatabaseStorage implements IStorage {
  // User operations (required for Replit Auth)
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user;
  }

  async upsertUser(userData: UpsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(userData)
      .onConflictDoUpdate({
        target: users.id,
        set: {
          ...userData,
          updatedAt: new Date(),
        },
      })
      .returning();
    return user;
  }

  // User management
  async createUser(userData: InsertUser): Promise<User> {
    const [user] = await db.insert(users).values(userData).returning();
    return user;
  }

  async updateUser(id: string, data: Partial<InsertUser>): Promise<User> {
    const [user] = await db
      .update(users)
      .set({ ...data, updatedAt: new Date() })
      .where(eq(users.id, id))
      .returning();
    return user;
  }

  async getUsersByRole(role: string): Promise<User[]> {
    return await db.select().from(users).where(eq(users.role, role as any));
  }

  async getUsersByManager(managerId: string): Promise<User[]> {
    return await db.select().from(users).where(eq(users.managerId, managerId));
  }

  async getDirectReports(managerId: string): Promise<User[]> {
    return await db.select().from(users).where(eq(users.managerId, managerId));
  }

  // Review cycle management
  async createReviewCycle(cycleData: InsertReviewCycle): Promise<ReviewCycle> {
    const [cycle] = await db.insert(reviewCycles).values(cycleData).returning();
    return cycle;
  }

  async getActiveReviewCycle(): Promise<ReviewCycle | undefined> {
    const [cycle] = await db
      .select()
      .from(reviewCycles)
      .where(eq(reviewCycles.isActive, true));
    return cycle;
  }

  async getAllReviewCycles(): Promise<ReviewCycle[]> {
    return await db.select().from(reviewCycles).orderBy(desc(reviewCycles.createdAt));
  }

  async updateReviewCycle(id: string, data: Partial<InsertReviewCycle>): Promise<ReviewCycle> {
    const [cycle] = await db
      .update(reviewCycles)
      .set(data)
      .where(eq(reviewCycles.id, id))
      .returning();
    return cycle;
  }

  // Employee review management
  async createEmployeeReview(reviewData: InsertEmployeeReview): Promise<EmployeeReview> {
    const [review] = await db.insert(employeeReviews).values(reviewData).returning();
    return review;
  }

  async getEmployeeReview(employeeId: string, cycleId: string): Promise<EmployeeReview | undefined> {
    const [review] = await db
      .select()
      .from(employeeReviews)
      .where(
        and(
          eq(employeeReviews.employeeId, employeeId),
          eq(employeeReviews.cycleId, cycleId)
        )
      );
    return review;
  }

  async getEmployeeReviewById(id: string): Promise<EmployeeReview | undefined> {
    const [review] = await db
      .select()
      .from(employeeReviews)
      .where(eq(employeeReviews.id, id));
    return review;
  }

  async updateEmployeeReview(id: string, data: Partial<InsertEmployeeReview>): Promise<EmployeeReview> {
    const [review] = await db
      .update(employeeReviews)
      .set({ ...data, updatedAt: new Date() })
      .where(eq(employeeReviews.id, id))
      .returning();
    return review;
  }

  async getReviewsByManager(managerId: string, cycleId: string): Promise<EmployeeReview[]> {
    return await db
      .select()
      .from(employeeReviews)
      .innerJoin(users, eq(employeeReviews.employeeId, users.id))
      .where(
        and(
          eq(users.managerId, managerId),
          eq(employeeReviews.cycleId, cycleId)
        )
      );
  }

  async getReviewsByStatus(status: string, cycleId: string): Promise<EmployeeReview[]> {
    return await db
      .select()
      .from(employeeReviews)
      .where(
        and(
          eq(employeeReviews.status, status as any),
          eq(employeeReviews.cycleId, cycleId)
        )
      );
  }

  async getPendingApprovals(managerId: string): Promise<EmployeeReview[]> {
    return await db
      .select()
      .from(employeeReviews)
      .innerJoin(users, eq(employeeReviews.employeeId, users.id))
      .where(
        and(
          eq(users.managerId, managerId),
          or(
            eq(employeeReviews.status, "manager_review"),
            eq(employeeReviews.status, "meeting_scheduled")
          )
        )
      );
  }

  // 360-degree feedback
  async createFeedback(feedbackData: InsertFeedback): Promise<Feedback> {
    const [feedbackRecord] = await db.insert(feedback).values(feedbackData).returning();
    return feedbackRecord;
  }

  async getFeedbackForReview(reviewId: string): Promise<Feedback[]> {
    return await db
      .select()
      .from(feedback)
      .where(eq(feedback.reviewId, reviewId));
  }

  async getFeedbackByUser(userId: string, cycleId: string): Promise<Feedback[]> {
    return await db
      .select()
      .from(feedback)
      .innerJoin(employeeReviews, eq(feedback.reviewId, employeeReviews.id))
      .where(
        and(
          eq(feedback.feedbackFromId, userId),
          eq(employeeReviews.cycleId, cycleId)
        )
      );
  }

  // Meeting management
  async createMeeting(meetingData: InsertMeeting): Promise<Meeting> {
    const [meeting] = await db.insert(meetings).values(meetingData).returning();
    return meeting;
  }

  async getMeetingsByEmployee(employeeId: string): Promise<Meeting[]> {
    return await db
      .select()
      .from(meetings)
      .where(eq(meetings.employeeId, employeeId))
      .orderBy(desc(meetings.scheduledAt));
  }

  async getMeetingsByManager(managerId: string): Promise<Meeting[]> {
    return await db
      .select()
      .from(meetings)
      .where(eq(meetings.managerId, managerId))
      .orderBy(desc(meetings.scheduledAt));
  }

  async updateMeeting(id: string, data: Partial<InsertMeeting>): Promise<Meeting> {
    const [meeting] = await db
      .update(meetings)
      .set(data)
      .where(eq(meetings.id, id))
      .returning();
    return meeting;
  }

  // Appeal management
  async createAppeal(appealData: InsertAppeal): Promise<Appeal> {
    const [appeal] = await db.insert(appeals).values(appealData).returning();
    return appeal;
  }

  async getAppealsByEmployee(employeeId: string): Promise<Appeal[]> {
    return await db
      .select()
      .from(appeals)
      .where(eq(appeals.employeeId, employeeId))
      .orderBy(desc(appeals.createdAt));
  }

  async getAppealsByManager(managerId: string): Promise<Appeal[]> {
    return await db
      .select()
      .from(appeals)
      .where(eq(appeals.managerId, managerId))
      .orderBy(desc(appeals.createdAt));
  }

  async updateAppeal(id: string, data: Partial<InsertAppeal>): Promise<Appeal> {
    const [appeal] = await db
      .update(appeals)
      .set({ ...data, updatedAt: new Date() })
      .where(eq(appeals.id, id))
      .returning();
    return appeal;
  }

  // Activity logging
  async logActivity(activityData: InsertActivityLog): Promise<ActivityLog> {
    const [activity] = await db.insert(activityLogs).values(activityData).returning();
    return activity;
  }

  async getRecentActivities(limit: number = 10): Promise<ActivityLog[]> {
    return await db
      .select()
      .from(activityLogs)
      .orderBy(desc(activityLogs.createdAt))
      .limit(limit);
  }

  async getActivitiesByUser(userId: string): Promise<ActivityLog[]> {
    return await db
      .select()
      .from(activityLogs)
      .where(eq(activityLogs.userId, userId))
      .orderBy(desc(activityLogs.createdAt));
  }

  // Analytics
  async getReviewStats(cycleId: string): Promise<any> {
    const totalEmployees = await db
      .select({ count: count() })
      .from(employeeReviews)
      .where(eq(employeeReviews.cycleId, cycleId));

    const completedReviews = await db
      .select({ count: count() })
      .from(employeeReviews)
      .where(
        and(
          eq(employeeReviews.cycleId, cycleId),
          eq(employeeReviews.status, "completed")
        )
      );

    const averageRating = await db
      .select({ avg: avg(employeeReviews.finalRating) })
      .from(employeeReviews)
      .where(
        and(
          eq(employeeReviews.cycleId, cycleId),
          eq(employeeReviews.status, "completed")
        )
      );

    return {
      totalEmployees: totalEmployees[0]?.count || 0,
      completedReviews: completedReviews[0]?.count || 0,
      averageRating: averageRating[0]?.avg || 0,
    };
  }

  async getRatingDistribution(cycleId: string): Promise<any> {
    return await db
      .select({
        rating: employeeReviews.finalRating,
        count: count(),
      })
      .from(employeeReviews)
      .where(
        and(
          eq(employeeReviews.cycleId, cycleId),
          eq(employeeReviews.status, "completed")
        )
      )
      .groupBy(employeeReviews.finalRating);
  }

  async getDepartmentPerformance(cycleId: string): Promise<any> {
    return await db
      .select({
        department: users.department,
        averageRating: avg(employeeReviews.finalRating),
        employeeCount: count(),
      })
      .from(employeeReviews)
      .innerJoin(users, eq(employeeReviews.employeeId, users.id))
      .where(
        and(
          eq(employeeReviews.cycleId, cycleId),
          eq(employeeReviews.status, "completed")
        )
      )
      .groupBy(users.department);
  }
}

export const storage = new DatabaseStorage();

